import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import Modal from 'react-bootstrap/Modal'

// done with Bootstrap modal
class Checkout extends Component {

  constructor(props, context) {
    super(props, context);

    this.state = {
      show: false,
    };

    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);
  }

  handleClose(event) {
    this.setState({
      show: false,
      errorMessage: ''
    });
  }

  handleShow() {
    this.setState({
      show: true,
      errorMessage: ''
    });
  }





  render() {



    return (
      <>
        <element>

          {/* <button name="addtobasket" onClick={this.handleShow} type="button" class="btn btn-primary signOutButton">
            Shopping Basket <span className="badge badge-light ">{this.props.copyState.basket.length}</span>
          </button> */}


          <Modal show={this.state.show} onHide={this.handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>Checkout</Modal.Title>
            </Modal.Header>



            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
            </Button>
              <Button onClick={(evt) => this.checkout("checkout")} variant="primary" >
                Checkout Now
            </Button>
            </Modal.Footer>

          </Modal>
        </element>
      </>
    );
  }

}


export default Checkout;
