import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import Modal from 'react-bootstrap/Modal'
// done with Bootstrap modal
class Shopping_basket extends Component {

  constructor(props, context) {
     super(props, context);

    this.state = {
      show: false,
      errorMessage: '',
      email: '',
      password: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = (event) => {
    const name = event.target.name
    this.setState({
      [name]: event.target.value,
      errorMessage: ''
    })
  }

  handleClose(event) {
    this.setState({
      show: false,
      errorMessage: ''
    });
  }

  handleShow() {
    this.setState({ show: true,
      errorMessage: ''
     });
  }

  handleSubmit = (event) => {
    var data = {
      email: this.state.email,
      password: this.state.password
    }
    if (data.email === "" || data.password === "") {
      this.setState({ errorMessage: "All fields need to be complete" })
    } else {
  //     this.signInHandler(registerData)
  //   }
  // }


  // signInHandler = (data) => {
    var em = "Email Address Not Found"
    var j = this.props.copyState.customerList.length - 1
    for (let i = 0; i < j; i++) {
      let e = this.props.copyState.customerList[i].email
      let p = this.props.copyState.customerList[i].password
      if (e === data.email) {
        em = "Password Incorrect"
        if (p === data.password) {
          this.props.copyState.customer.firstName = this.props.copyState.customerList[i].firstName
          this.props.copyState.customer.secondName = this.props.copyState.customerList[i].secondName
          this.props.copyState.customer.email = this.props.copyState.customerList[i].email
          this.props.copyState.customer.password = this.props.copyState.customerList[i].password
          this.props.buttonHandlerFunction("signIn", this.props.copyState)
          // this.setState({ errorMessage: "" })
          this.handleClose()
        }
      }
      this.setState({ errorMessage: em })
    }
  }
  }


    render() {

        // const basket = require('./images/shopping_basket_green.png');

      return (
        <>
          <element>

          <button name="addtobasket" onClick={this.handleShow} type="button" class="btn btn-primary">
  Shopping Basket <span class="badge badge-light">{this.props.copyState.basket.length}</span>
</button>


              {/* <Button onClick={this.handleShow} variant="contained" color="alert" >
                Basket 
        </Button>  */}
{/* <button src={basket} onClick={this.handleShow} alt="shopping basket button"></button> */}



            <Modal show={this.state.show} onHide={this.handleClose}>
              <Modal.Header closeButton>
                <Modal.Title>Your Shopping Basket</Modal.Title>
              </Modal.Header>

              <form onSubmit={this.handleSubmit} className="createAccount">
                <input type="text" name="email"
                  placeholder="Enter your email"
                  onChange={this.handleChange}
                  className="form-control registerTextBox">
                </input>
                <input type="text" name="password"
                  placeholder="Enter your password"
                  onChange={this.handleChange}
                  className="form-control registerTextBox">
                </input>

                <p className="redText centered">{this.state.errorMessage}</p>

                <Modal.Footer>
                  <Button variant="secondary" onClick={this.handleClose}>
                    Cancel
            </Button>
                  <Button onClick={this.handleSubmit} variant="primary" >
                    Sign In
            </Button>
                </Modal.Footer>
              </form>
            </Modal>
          </element>
        </>
      );
    }
  }

  export default Shopping_basket;
