import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import Modal from 'react-bootstrap/Modal'
// done with Bootstrap modal
class Basket extends Component {

    // constructor(props, context) {
    //     super(props, context);

    state = {
        // show: false,
        errorMessage: '',
        email: '',
        password: ''
    };


    // this.handleChange = this.handleChange.bind(this);
    //  }

    handleChange = (event) => {
        const name = event.target.name
        this.setState({
            [name]: event.target.value,
            errorMessage: ''
        })
    }

    totalPrice=()=>{
        var totalPrice=0
        const basket = this.props.basket
        for (let i=0; i<basket.length;i++){
            totalPrice = totalPrice + (basket[i].qty * basket[i].price)
        }
        return totalPrice
    }


    render() {
        return (
            <>
                <element>

                    <Modal show={this.props.openClose} onHide={this.props.closeModal}>
                        <Modal.Header closeButton>
                            <Modal.Title>Shopping Basket</Modal.Title>
                        </Modal.Header>

                        number of items in basket {this.props.basket.length}
                        {this.props.basket.map(function(elem,i) {
                            return (<li>
                            {elem.qty}{" x "}
                            {elem.title}{" "}
                            {elem.format}{" @ £"}
                            {elem.price}{" = £"}
                            {elem.price * elem.qty}
                            </li>)
                        })
                    }
                    Total Price = {this.totalPrice()}

                        <Modal.Footer>
                            <Button variant="secondary" onClick={this.props.closeModal}>
                                Cancel
            </Button>
                            {/* <Button onClick={this.props.openModal("signin")} variant="primary" > */}
                            <Button onClick={() => { this.props.openModal("signin") }} variant="primary" >Sign In
            </Button>
                        </Modal.Footer>
                        {/* </form>  */}
                    </Modal>
                </element>
            </>
        );
    }
}

export default Basket;
