import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import Modal from 'react-bootstrap/Modal'

class RegisterBootstrap extends Component {

  constructor(props, context) {
   super(props, context);

    this.state = {
      show: false,
      firstName: '',
      secondName: '',
      email: '',
      password: ''
    };

    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  inputHandler = (event) => {
    alert(event.firstName)
  }

  handleSubmit = (event) =>{
    alert (this.state.firstName + ' ' + this.state.secondName + ' ' + this.state.email + ' ' + this.state.password)
  }


  handleClose(event) {
    this.setState({ show: false });
    // this.dealWithInputs(event)
  }

  handleShow() {
    this.setState({ show: true });
  }


  render() {
    return (
       <>
        <Button onClick={this.handleShow} variant="contained" color="primary">
          Register
        </Button>

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Create an Account</Modal.Title>
          </Modal.Header>
          {/* <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body> */}

          <form onSubmit={this.handleSubmit} className="createAccount">

            <input type="text" name="firstName"
              placeholder="Enter first name"
              onChange={this.handleChange}
              className="form-control">
            </input>
            <input type="text" name="secondName"
              placeholder="Enter second name"
              onChange={this.handleChange}
              className="form-control">
            </input>
            <input type="text" name="email"
              placeholder="Enter your email"
              onChange={this.handleChange}
              className="form-control">
            </input>
            <input type="text" name="password"
              placeholder="Enter your password"
              onChange={this.handleChange}
              className="form-control">
            </input>




            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Cancel
            </Button>
              {/* <Button variant="primary" onClick={this.handleClose}> */}
              <Button type="submit" value="Submit" name="submit" variant="primary" onClick={this.handleChange}>
                Create Account
            </Button>
            </Modal.Footer>
          </form>
        </Modal>
      </>
    );
  }
}

export default RegisterBootstrap;
