import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import Modal from 'react-bootstrap/Modal'
// done with Bootstrap modal
class Shoppingbasket extends Component {

  constructor(props, context) {
    super(props, context);

    this.state = {
      show: false,
      errorMessage: '',
      email: '',
      password: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = (event) => {
    const name = event.target.name
    this.setState({
      [name]: event.target.value,
      errorMessage: ''
    })
  }

  handleClose(event) {
    this.setState({
      show: false,
      errorMessage: ''
    });
  }

  handleShow() {
    this.setState({
      show: true,
      errorMessage: ''
    });
  }

  handleSubmit = (event) => {
    var data = {
      email: this.state.email,
      password: this.state.password
    }
    if (data.email === "" || data.password === "") {
      this.setState({ errorMessage: "All fields need to be complete" })
    } else {
      var em = "Email Address Not Found"
      var j = this.props.copyState.customerList.length - 1
      for (let i = 0; i < j; i++) {
        let e = this.props.copyState.customerList[i].email
        let p = this.props.copyState.customerList[i].password
        if (e === data.email) {
          em = "Password Incorrect"
          if (p === data.password) {
            this.props.copyState.customer.firstName = this.props.copyState.customerList[i].firstName
            this.props.copyState.customer.secondName = this.props.copyState.customerList[i].secondName
            this.props.copyState.customer.email = this.props.copyState.customerList[i].email
            this.props.copyState.customer.password = this.props.copyState.customerList[i].password
            this.props.buttonHandlerFunction("signIn", this.props.copyState)
            this.handleClose()
          }
        }
        this.setState({ errorMessage: em })
      }
    }
  }




  render() {

    var IDs = []
    var subTotalCost = 0
    for (let i = 0; i < this.props.copyState.basket.length; i++) {
      for (let j = 0; j < this.props.copyState.jb.length; j++) {
        var a = this.props.copyState.basket[i].sku
        var b = this.props.copyState.jb[j].sku
        if (a === b) {
          var t = { qty: this.props.copyState.basket[i].qty, sku: j }
          IDs.push(t)
          subTotalCost = subTotalCost + this.props.copyState.basket[i].qty * this.props.copyState.jb[j].price
        }
      }
    }


    return (
      <>
        <element>

          <button name="addtobasket" onClick={this.handleShow} type="button" class="btn btn-primary">
            Shopping Basket <span class="badge badge-light">{this.props.copyState.basket.length}</span>
          </button>


          <Modal show={this.state.show} onHide={this.handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>Your Shopping Basket</Modal.Title>
            </Modal.Header>


            {
              IDs.map((item, i) => {
                return <element className="buttonright">
                {item.qty} x {this.props.copyState.jb[item.sku].title}
                <button type="button" class="buttonRowButton btn btn-danger btn-smt">Remove</button>
                 </element>

              })

            }

            <div className="shoppingbasket-right">Cost of goods = Â£{subTotalCost}</div>




            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Close
            </Button>
              <Button onClick={this.handleSubmit} variant="primary" >
                Checkout Now
            </Button>
            </Modal.Footer>

          </Modal>
        </element>
      </>
    );
  }

}


export default Shoppingbasket;
