import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import Modal from 'react-bootstrap/Modal'
import { isThisExpression } from '@babel/types';
// done with Bootstrap modal
class Basket extends Component {

    // constructor(props, context) {
    //     super(props, context);

    state = {
        // show: false,
        errorMessage: '',
        email: '',
        password: ''
    };

    // this.handleChange = this.handleChange.bind(this);
    //  }

    handleChange = (event) => {
        const name = event.target.name
        this.setState({
            [name]: event.target.value,
            errorMessage: ''
        })
    }

    totalPrice = () => {
        var totalPrice = 0
        const basket = this.props.basket
        for (let i = 0; i < basket.length; i++) {
            totalPrice = totalPrice + (basket[i].qty * basket[i].cost)
        }
        return totalPrice
    }

    render() {

        const products = this.props.basket.map(function (elem, i) {
            return (
                < span key={i}><li className="shoppingbasket">
                    {elem.qty}{" x "}
                    {elem.title}{" "}
                    {elem.format}{" @ £"}
                    {elem.cost}{" each = £"}
                    {elem.cost * elem.qty}
                </li></span >)
        })

        const totalItems = () => {
            var total = 0
            for (let i = 0; i < this.props.basket.length; i++) {
                total = total + this.props.basket[i].qty
            }
        }

        return (
            <>
                <element>

                    <Modal show={this.props.openClose} onHide={this.props.closeModal}>
                        <Modal.Header closeButton>
                            <Modal.Title>Shopping Basket</Modal.Title>
                        </Modal.Header>

                        <p className="shoppingbasket"> number of items in basket {totalItems}</p>

                        {products}

                        <p className="shoppingbasket-right">
                            Total Price = £{this.totalPrice()}
                        </p>

                        <Modal.Footer>
                            <Button onClick={this.props.closeModal}>
                                Cancel
            </Button>
                            <Button onClick={() => { this.props.openModal("signin") }} >REGISTER
                            </Button>
                            <Button onClick={() => { this.props.openModal("signin") }} >Sign In
            </Button>
                        </Modal.Footer>
                    </Modal>
                </element>
            </>
        );
    }
}

export default Basket;
