import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import Modal from 'react-bootstrap/Modal'

class RegisterBootstrap extends Component {

  constructor(props, context) {
    super(props, context);

    this.state = {
      show: false,
      errorMessage: "",
      firstName: '',
      secondName: '',
      email: '',
      password: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = (event)=>{
    const name=event.target.name
    this.setState({[name]:event.target.value,
      errorMessage:''
    })
  }

  handleClose(event) {
    this.setState({ show: false });
  }

  handleShow() {
    this.setState({ show: true });
  }

  handleSubmit = (event) => {
    const firstName = this.state.firstName
    const secondName = this.state.secondName
    const email = this.state.email
    const password = this.state.password
    // alert(firstName + ' ' + secondName + ' ' + email + ' ' + password)
    if (firstName==="" || secondName==="" || email==="" || password==="") {
      this.setState({errorMessage:"All fields need to be complete"})
    }

  }

  render() {
    return (
      <>
        <Button onClick={this.handleShow} variant="contained" color="primary">
          Register
        </Button>

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Create an Account</Modal.Title>
          </Modal.Header>

          <form onSubmit={this.handleSubmit} className="createAccount">
            <input type="text" name="firstName"
              placeholder="Enter first name"
              onChange={this.handleChange}
              className="form-control">
            </input>
            <input type="text" name="secondName"
              placeholder="Enter second name"
              onChange={this.handleChange}
              className="form-control">
            </input>
            <input type="text" name="email"
              placeholder="Enter your email"
              onChange={this.handleChange}
              className="form-control">
            </input>
            <input type="text" name="password"
              placeholder="Enter your password"
              onChange={this.handleChange}
              className="form-control">
            </input>

            <p className="redtext">{this.state.errorMessage}</p>

            <Modal.Footer>
              <Button variant="secondary" onClick={this.handleClose}>
                Cancel
            </Button>
              {/* <Button type="submit" value="Submit" name="submit" variant="primary" onClick={this.handleClose}> */}
              <Button onClick={this.handleSubmit} variant="primary" >
                Create Account
            </Button>
            </Modal.Footer>
          </form>
        </Modal>
      </>
    );
  }
}

export default RegisterBootstrap;
