import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import Modal from 'react-bootstrap/Modal'
import ReactFlagsSelect from 'react-flags-select';
import 'react-flags-select/css/react-flags-select.css';

// done with Bootstrap modal
class Checkout extends Component {

    constructor(props, context) {
        super(props, context);

        this.state = {
            show: false,
        };

        this.handleShow = this.handleShow.bind(this);
        this.handleClose = this.handleClose.bind(this);
    }

    handleClose(event) {
        this.setState({
            show: false,
            errorMessage: ''
        });
    }

    handleShow() {
        this.setState({
            show: true,
            errorMessage: ''
        });

        var em = "Please Register or Login"
        if (this.props.copyState.login) {
            em = "Already Logged In"
        }
        this.setState({
            errorMessage: em
        })

    }

    checkoutHandler = () => {
        this.props.buttonHandlerFunction("checkout")
    }



    render() {



        return (
            <>
                <element>

                    < Button onClick={this.handleShow} variant="primary" >
                        Checkout Now
            </Button>


                    <Modal show={this.state.show} onHide={this.handleClose}>
                        <Modal.Header closeButton>
                            <Modal.Title>Checkout</Modal.Title>
                        </Modal.Header>

                        <form onSubmit={this.handleSubmit} className="createAccount">
                            <span>
                                <input type="text" name="firstName"
                                    // placeholder={this.props.copyState.customer.firstName}
                                    onChange={this.handleChange}
                                    className="form-control registerTextBox">
                                </input>
                                <input type="text" name="secondName"
                                    placeholder={this.props.copyState.customer.secondName}
                                    onChange={this.handleChange}
                                    className="form-control registerTextBox">
                                </input>
                            </span>
                            <input type="text" name="email"
                                placeholder={this.props.copyState.customer.email}
                                onChange={this.handleChange}
                                className="form-control registerTextBox">
                            </input>
                            <input type="text" name="address1"
                                placeholder="Address Line 1"
                                onChange={this.handleChange}
                                className="form-control registerTextBox">
                            </input>
                            <input type="text" name="address2"
                                placeholder="Address Line 2"
                                onChange={this.handleChange}
                                className="form-control registerTextBox">
                            </input>
                            <input type="text" name="address3"
                                placeholder="Address Line 3"
                                onChange={this.handleChange}
                                className="form-control registerTextBox">
                            </input>
                            <input type="text" name="address4"
                                placeholder="Address Line 4"
                                onChange={this.handleChange}
                                className="form-control registerTextBox">
                            </input>
                            <input type="text" name="address5"
                                placeholder="Post Code"
                                onChange={this.handleChange}
                                className="form-control registerTextBox">
                            </input>
                            <ReactFlagsSelect
                                defaultCountry="GB" />
                            <p className="redText centered">{this.state.errorMessage}</p>
                        </form>


                        <Modal.Footer>
                            <Button variant="secondary" onClick={this.handleClose}>
                                Close
            </Button>
                        </Modal.Footer>

                    </Modal>
                </element>
            </>
        );
    }

}


export default Checkout;
