import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import Modal from 'react-bootstrap/Modal'

class RegisterBootstrap extends Component {

  constructor(props, context) {
    super(props, context);

    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);

    this.state = {
      show: false,
    };
  }

  dealWithInputs = (event) => {
    alert(event.firstName)
  }

  handleClose(event) {
    this.setState({ show: false });
  }

  handleShow() {
    this.setState({ show: true });
  }

  inputBoxChanged() {
    return
  }

  render() {
    return (
      <>
        <Button onClick={this.dealWithInputs} variant="contained" color="primary">
          Register
        </Button>

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Create an Account</Modal.Title>
          </Modal.Header>

          <form onSubmit={this.handleSubmit} className="createAccount">

            <input type="text" ref="firstName"
              placeholder="Enter first name"
              onChange={this.inputBoxChanged}
              className="form-control">
            </input>
            <input type="text" ref="secondName"
              placeholder="Enter second name"
              onChange={this.inputBoxChanged}
              className="form-control">
            </input>
            <input type="text" ref="email"
              placeholder="Enter your email"
              onChange={this.inputBoxChanged}
              className="form-control">
            </input>
            <input type="text" ref="password"
              placeholder="Enter your password"
              onChange={this.inputBoxChanged}
              className="form-control">
            </input>




            <Modal.Footer>
              <Button type="submit" variant="secondary" onClick={this.handleClose}>
                Cancel
            </Button>
              <Button variant="primary" onClick={this.handleClose}>
                Create Account
            </Button>
            </Modal.Footer>
          </form>
        </Modal>
      </>
    );
  }
}

export default RegisterBootstrap;
